<?php/** * @see Beausy_Service_Paypal_Data_Abstract */require_once 'Beausy/Service/Paypal/Data/Abstract.php';/** * * Beausy * * @author    Beau Hastings <beausy@gmail.com> * @copyright Beau Hastings * @license   GNU GPL version 2 {@link http://www.gnu.org/licenses/gpl-2.0.html} * */class Beausy_Service_Paypal_Data_BillingAgreementDetails extends Beausy_Service_Paypal_Data_Abstract{  /**   * Payment number or index   * @var integer   */  private $_paymentIndex = 0;  /**   * Type of billing agreement.   * @var string   */  private $_billingType;  /**   * Description of goods or services associated with the billing agreement, which is required for each recurring payment billing agreement   * @var string   */  private $_billingAgreementDescription;  /**   * Specifies type of PayPal payment you require for the billing agreement   * @var string   */  private $_paymentType;  /**   * Custom annotation field for your own use   * @var string   */  private $_billingAgreementCustom;  /**   * Set payment index   *   * @param   int  $value   * @return  Beausy_Service_Paypal_Data_BillingAgreementDetails   */  public function setPaymentIndex($value)  {    $value = (int) $value;    $this->_paymentIndex = $value;    return $this;  }  /**   * Retrieve payment index   * @return int   */  public function getPaymentIndex()  {    return $this->_paymentIndex;  }  /**   * Set type of billing agreement   *   * @param   string  $value   * @return  Beausy_Service_Paypal_Data_BillingAgreementDetails   */  public function setBillingType($value)  {    $this->_billingType = $value;    return $this;  }  /**   * Retrieve type of billing agreement   * @return string   */  public function getBillingType()  {    return $this->_billingType;  }  /**   * Set description of goods or services associated with the billing agreement   *   * @param   string  $value   * @return  Beausy_Service_Paypal_Data_BillingAgreementDetails   */  public function setBillingAgreementDescription($value)  {    $this->_billingAgreementDescription = $value;    return $this;  }  /**   * Retrieve description of goods or services associated with the billing agreement   * @return string   */  public function getBillingAgreementDescription()  {    return $this->_billingAgreementDescription;  }  /**   * Set type of PayPal payment you require for the billing agreement   *   * @param   string  $value   * @return  Beausy_Service_Paypal_Data_BillingAgreementDetails   */  public function setPaymentType($value)  {    $this->_paymentType = $value;    return $this;  }  /**   * Retrieve type of PayPal payment you require for the billing agreement   * @return string   */  public function getPaymentType()  {    return $this->_paymentType;  }  /**   * Set custom annotation field   *   * @param   string  $value   * @return  Beausy_Service_Paypal_Data_BillingAgreementDetails   */  public function setBillingAgreementCustom($value)  {    $this->_billingAgreementCustom = $value;    return $this;  }  /**   * Retrieve custom annotation field   * @return string   */  public function getBillingAgreementCustom()  {    return $this->_billingAgreementCustom;  }  /**   * Returns this class as an array   *   * @return array   */  public function toArray()  {    $array = array();    $array['L_BILLINGTYPE' . $this->_paymentIndex] = $this->_billingType;    $array['L_BILLINGAGREEMENTDESCRIPTION' . $this->_paymentIndex] = $this->_billingAgreementDescription;    // Optional    if (!empty($this->_paymentType))      $array['L_PAYMENTTYPE' . $this->_paymentIndex] = $this->_paymentType;    if (!empty($this->_billingAgreementCustom))      $array['L_BILLINGAGREEMENTCUSTOM' . $this->_paymentIndex] = $this->_billingAgreementCustom;    return $array;  }}