<?php/** * @see Beausy_Service_Paypal_Data_Abstract */require_once 'Beausy/Service/Paypal/Data/Abstract.php';/** * * Beausy * * @author    Beau Hastings <beausy@gmail.com> * @copyright Beau Hastings * @license   GNU GPL version 2 {@link http://www.gnu.org/licenses/gpl-2.0.html} * */class Beausy_Service_Paypal_Data_PaymentDetailsItem extends Beausy_Service_Paypal_Data_Abstract{  /**   * Payment details item index   * @var integer   */  private $_itemIndex = 0;  /**   * Item name   * @var string   */  private $_itemName;  /**   * Item description   * @var string   */  private $_itemDescription;  /**   * Cost of item   * @var string   */  private $_itemAmount;  /**   * Item number   * @var string   */  private $_itemNumber;  /**   * Item quantity   * @var integer   */  private $_itemQuantity;  /**   * Item sales tax   * @var string   */  private $_itemTaxAmount;  /**   * Set the payment details item index   *   * @param   string  $value   * @return  Beausy_Service_Paypal_Data_PaymentDetailsItem   */  public function setItemIndex($value)  {    $value = (int) $value;    $this->_itemIndex = $value;    return $this;  }  /**   * Retrieve the payment details item index   * @return string   */  public function getItemIndex()  {    return $this->_itemIndex;  }  /**   * Set item name   *   * @param   string  $value   * @return  Beausy_Service_Paypal_Data_PaymentDetailsItem   */  public function setItemName($value)  {    $this->_itemName = $value;    return $this;  }  /**   * Retrieve item name   * @return string   */  public function getItemName()  {    return $this->_itemName;  }  /**   * Set item description   *   * @param   string  $value   * @return  Beausy_Service_Paypal_Data_PaymentDetailsItem   */  public function setItemDescription($value)  {    $this->_itemDescription = $value;    return $this;  }  /**   * Retrieve item description   * @return string   */  public function getItemDescription()  {    return $this->_itemDescription;  }  /**   * Set cost of item   *   * @param   string  $value   * @return  Beausy_Service_Paypal_Data_PaymentDetailsItem   */  public function setItemAmount($value)  {    $this->_itemAmount = $value;    return $this;  }  /**   * Retrieve cost of item   * @return string   */  public function getItemAmount()  {    return $this->_itemAmount;  }  /**   * Set item number   *   * @param   string  $value   * @return  Beausy_Service_Paypal_Data_PaymentDetailsItem   */  public function setItemNumber($value)  {    $this->_itemNumber = $value;    return $this;  }  /**   * Retrieve item number   * @return  string   */  public function getItemNumber()  {    return $this->_itemNumber;  }  /**   * Set item quantity   *   * @param   integer   $value   * @return  Beausy_Service_Paypal_Data_PaymentDetailsItem   */  public function setItemQuantity($value)  {    $this->_itemQuantity = (int) $value;    return $this;  }  /**   * Retrieve item quantity   * @return integer   */  public function getItemQuantity()  {    return $this->_itemQuantity;  }  /**   * Set item sales tax   *   * @param   string  $value   * @return  Beausy_Service_Paypal_Data_PaymentDetailsItem   */  public function setItemTaxAmount($value)  {    $this->_itemTaxAmount = $value;    return $this;  }  /**   * Retrieve item sales tax   * @return string   */  public function getItemTaxAmount()  {    return $this->_itemTaxAmount;  }  /**   * Returns this class as an array   *   * @return array   */  public function toArray()  {    $array = array();    // Optional fields (key=PayPal field name, value=internal variable name)    $optional = array(      'L_NAME'    => '_itemName',      'L_DESC'    => '_itemDescription',      'L_AMT'     => '_itemAmount',      'L_NUMBER'  => '_itemNumber',      'L_QTY'     => '_itemQuantity',      'L_TAXAMT'  => '_itemTaxAmount');    // Apply optional options and append item index number to each key as required by the PayPal API    foreach($optional as $option => $value)      if (isset($this->$value))        $array[$option . $this->_itemIndex] = $this->$value;    return $array;  }}