<?php/** * @see Beausy_Service_Paypal_Data_Abstract */require_once 'Beausy/Service/Paypal/Data/Abstract.php';/** * * Beausy * * @author    Beau Hastings <beausy@gmail.com> * @copyright Beau Hastings * @license   GNU GPL version 2 {@link http://www.gnu.org/licenses/gpl-2.0.html} * */class Beausy_Service_Paypal_Data_SellerDetailsType extends Beausy_Service_Paypal_Data_Abstract{  /**   * Seller details index   * @var integer   */  private $_itemIndex = 0;  /**   * The unique non-changing identifier for the seller at the marketplace site   * @var string   */  private $_sellerId;  /**   * The current name of the seller or business at the marketplace site   * @var string   */  private $_sellerUsername;  /**   * Date when the seller registered with the marketplace   * @var string   */  private $_sellerRegistrationDate;  /**   * Set seller details index   *   * @param   string  $value   * @return  Beausy_Service_Paypal_Data_SellerDetailsType   */  public function setItemIndex($value)  {    $value = (int) $value;    $this->_itemIndex = $value;    return $this;  }  /**   * Retrieve seller details index   * @return string   */  public function getItemIndex()  {    return $this->_itemIndex;  }  /**   * Set unique non-changing identifier for the seller at the marketplace site   *   * @param   string  $value   * @return  Beausy_Service_Paypal_Data_SellerDetailsType   */  public function setSellerId($value)  {    $this->_sellerId = $value;    return $this;  }  /**   * Retrieve unique non-changing identifier for the seller at the marketplace site   * @return string   */  public function getSellerId()  {    return $this->_sellerId;  }  /**   * Set current name of the seller or business at the marketplace site   *   * @param   string  $value   * @return  Beausy_Service_Paypal_Data_SellerDetailsType   */  public function setSellerUsername($value)  {    $this->_sellerUsername = $value;    return $this;  }  /**   * Retrieve current name of the seller or business at the marketplace site   * @return string   */  public function getSellerUsername()  {    return $this->_sellerUsername;  }  /**   * Set date when the seller registered with the marketplace   *   * @param   string  $value   * @return  Beausy_Service_Paypal_Data_SellerDetailsType   */  public function setSellerRegistrationDate($value)  {    $this->_sellerRegistrationDate = $value;    return $this;  }  /**   * Retrieve date when the seller registered with the marketplace   * @return string   */  public function getSellerRegistrationDate()  {    return $this->_sellerRegistrationDate;  }  /**   * Returns this class as an array   *   * @return array   */  public function toArray()  {    $array = array();    // Optional fields (key=PayPal field name, value=internal variable name)    $optional = array(      array('prefix' => 'PAYMENTREQUEST', 'suffix' => 'SELLERID',               'variable' => '_sellerId'),      array('prefix' => 'PAYMENTREQUEST', 'suffix' => 'SELLERUSERNAME',         'variable' => '_sellerUsername'),      array('prefix' => 'PAYMENTREQUEST', 'suffix' => 'SELLERREGISTRATIONDATE', 'variable' => '_sellerRegistrationDate'),    );    // Apply optional options and append item index number to each key as required by the PayPal API    foreach($optional as $option)      if (isset($this->$option['variable']))        $array[$option['prefix'] . $this->_itemIndex . $option['suffix']] = $this->$option['variable'];    return $array;  }}