<?php/** * @see Beausy_Service_Paypal_Data_Abstract */require_once 'Beausy/Service/Paypal/Data/Abstract.php';/** * * Beausy * * @author    Beau Hastings <beausy@gmail.com> * @copyright Beau Hastings * @license   GNU GPL version 2 {@link http://www.gnu.org/licenses/gpl-2.0.html} * */class Beausy_Service_Paypal_Data_UserSelectedOptions extends Beausy_Service_Paypal_Data_Abstract{  /**   * The Yed/No option that was chosen by the buyer chose for insurance   * @var string   */  private $_insuranceOptionSelected;  /**   * If the buyer chose the default shipping option   * @var string   */  private $_shippingOptionIsDefault;  /**   * Shipping amount that was chosen by the buyer   * @var string   */  private $_shippingOptionAmount;  /**   * Name of the shipping option such as air or ground   * @var string   */  private $_shippingOptionName;  /**   * Set option that was chosen by the buyer chose for insurance   *   * @param   string  $value   * @return  Beausy_Service_Paypal_Data_UserSelectedOptions   */  public function setInsuranceOptionSelected($value)  {    $this->_insuranceOptionSelected = $value;    return $this;  }  /**   * Retrieve option that was chosen by the buyer chose for insurance   * @return string   */  public function getInsuranceOptionSelected()  {    return $this->_insuranceOptionSelected;  }  /**   * Set if the buyer chose the default shipping option   *   * @param   string  $value   * @return  Beausy_Service_Paypal_Data_UserSelectedOptions   */  public function setShippingOptionIsDefault($value)  {    $this->_shippingOptionIsDefault = $value;    return $this;  }  /**   * Retrieve if the buyer chose the default shipping option   * @return string   */  public function getShippingOptionIsDefault()  {    return $this->_shippingOptionIsDefault;  }  /**   * Set shipping amount that was chosen by the buyer   *   * @param   string  $value   * @return  Beausy_Service_Paypal_Data_UserSelectedOptions   */  public function setShippingOptionAmount($value)  {    $this->_shippingOptionAmount = $value;    return $this;  }  /**   * Retrieve shipping amount that was chosen by the buyer   * @return string   */  public function getShippingOptionAmount()  {    return $this->_shippingOptionAmount;  }  /**   * Set name of the shipping option such as air or ground   *   * @param   string  $value   * @return  Beausy_Service_Paypal_Data_UserSelectedOptions   */  public function setShippingOptionName($value)  {    $this->_shippingOptionName = $value;    return $this;  }  /**   * Retrieve name of the shipping option such as air or ground   * @return string   */  public function getShippingOptionName()  {    return $this->_shippingOptionName;  }  /**   * Returns this class as an array   *   * @return array   */  public function toArray()  {    $array = array();    // All fields are optional    if (!empty($this->_insuranceOptionSelected))      $array['INSURANCEOPTIONSELECTED'] = $this->_insuranceOptionSelected;    if (!empty($this->_shippingOptionIsDefault))      $array['SHIPPINGOPTIONISDEFAULT'] = $this->_shippingOptionIsDefault;    if (!empty($this->_shippingOptionAmount))      $array['SHIPPINGOPTIONAMOUNT'] = $this->_shippingOptionAmount;    if (!empty($this->_shippingOptionName))      $array['SHIPPINGOPTIONNAME'] = $this->_shippingOptionName;    return $array;  }}