<?php/** * @see Beausy_Service_Paypal_Response_Nvp_Abstract */require_once 'Beausy/Service/Paypal/Response/Nvp/Abstract.php';/** * * Beausy * * @author    Beau Hastings <beausy@gmail.com> * @copyright Beau Hastings * @license   GNU GPL version 2 {@link http://www.gnu.org/licenses/gpl-2.0.html} * */// https://cms.paypal.com/us/cgi-bin/?cmd=_render-content&content_ID=developer/e_howto_api_nvp_r_ManageRecurringPaymentsProfileStatusclass Beausy_Service_Paypal_Response_Nvp_ManageRecurringPaymentsProfileStatus extends Beausy_Service_Paypal_Response_Nvp_Abstract{  /**   * Recurring payments profile ID returned in the CreateRecurringPaymentsProfile response   * @var string   */  protected $_profileId;  /**   * Parses the fields of the Paypal API response   *   * @param array $response   * @return void   */  public function parse($response)  {    /*     * Parse fields that come with every Paypal response     */    parent::parse($response);    /*     * If Paypal returned an error there are no additional fields to parse     */    if ($this->isError())      return;    $this->_profileId = $response['PROFILEID'];  }  /**   * Retrieve the recurring payments profile ID returned in the CreateRecurringPaymentsProfile response   *   * @return string   */  public function getProfileId()  {    return $this->_profileId;  }}